import './App.css';
import { useState} from 'react';
import { UploadDownload } from './Upload-Download-page/Upload-Download-page.js';
import './Landing-page.css';
import axios from 'axios';


function App() {
  const [activeIndex, setActiveIndex] = useState(0);
  return (
    <>
      <Panel isActive={activeIndex === 0} onShow={() => setActiveIndex(0)}>
        <Landing onAction={() => setActiveIndex(1)} />
      </Panel>
      <Panel isActive={activeIndex === 1} onShow={() => setActiveIndex(1)}>
        <UploadDownload />
      </Panel>
    </>
  );
}

function Panel({ children, isActive, onShow }) {
  return (
    <section className="panel">
      {isActive ? <p>{children}</p> : <button onClick={onShow}>Show</button>}
    </section>
  );
}

export function Landing({ onAction }) { 


    const [AuthPin, setAuthPin] = useState('')
    const handleAuth = (v) => {
        if (v.length <= 5) {
            setAuthPin(v)
        }
    }

    const [file, setFile] = useState()

    const [upload, setUpload] = useState(false)

    const [codesubmit, setCodesubmit] = useState(false)

    function handleChange(event) {
        setFile(event.target.files[0])
    }

    function generateCode() {
      setUpload(true)
      onAction();
    }

    function handleSubmitCode(){
      setCodesubmit(true)
      onAction();
    }

    function handleSubmit(event) {
        event.preventDefault()
        const url = 'http://localhost:3000/uploadFile';
        const formData = new FormData();
        formData.append('file', file);
        formData.append('fileName', file.name);
        const config = {
          headers: {
            'content-type': 'multipart/form-data',
          },
        };
        axios.post(url, formData, config).then((response) => {
          console.log(response.data);
        });

      }


	return (
		<div class="center">
			<h1 className="font-link">Firefly</h1>
            <br></br>
            <div className="button-23">
            <div className="button-24">
              {
                upload ? (
                  <form onSubmit={handleSubmit}>
                    <input type="file" onChange={handleChange}/>
                  </form>
                ) : (
                  <button onClick={generateCode}>
                    Generate Code
                  </button>
                )
              }
            </div>

              <br></br>
              <br></br>
              <p>input pin:</p>
              <input
                  value={AuthPin}
                  onChange={(e) => handleAuth(e.target.value)}
                  className="space"
                  type="text"
                  id="pin"
              />
              <br></br>
              <br></br>
              <button onClick={handleSubmitCode} className="button-25">
                Submit
              </button>
            </div>
		</div>
	);
}

export default App;
