import './App.css';
import React, { useState, useRef, useEffect } from 'react'

import axios from 'axios';


function App() {
  const [activeIndex, setActiveIndex] = useState(0);

  // Use the useEffect hook to apply the CSS classes based on the condition
  useEffect(() => {
    if (activeIndex===0) {
      document.body.className = 'panel-landing';
    } else {
      document.body.className = 'bodyClass2';
    }

    // Cleanup function to remove the classes when the component is unmounted
    return () => {
      document.body.className = '';
    };
  }, [activeIndex]);




  return (
    <>
      <Panel isActive={activeIndex === 0}>
        <div>
          <Landing onAction={() => setActiveIndex(1)} />
        </div>  
      </Panel>

      <Panel isActive={activeIndex === 1}>
        <div className="panel1">
          <UploadDownload />
        </div>
      </Panel>
    </>
  );
}



function Panel({ children, isActive}) {
  return (
    <section className="panel">
      {isActive ? <p>{children}</p> : null}
    </section>
  );
}



export function Landing({ onAction }) { 

  const [AuthPin, setAuthPin] = useState('')

  const handleAuth = (v) => {
      if (v.length <= 5) {
          setAuthPin(v)
      }
  }

  const [, setUpload] = useState(false)
  const [, setCodesubmit] = useState(false)

  function generateCode() {
    setUpload(true)
    onAction();
  }

  function handleSubmitCode(){
    setCodesubmit(true)
    onAction();
  }

return (
  <div class="center">
    <h1 className="font-link">Firefly</h1>
          <br></br>
          <div className="button-23">
          <div>
            {
                <button className="button-24" onClick={generateCode}>
                  Generate Code
                </button>
            }
          </div>

            <br></br>
            <br></br>
            <p>input pin:</p>
            <input
                value={AuthPin}
                onChange={(e) => handleAuth(e.target.value)}
                className="space"
                type="number"
                id="pin"
                pattern="\d*"
                inputMode="numeric"
            />
            <br></br>
            <br></br>
            <button
              onClick={handleSubmitCode}
              className={`button-25${AuthPin.length !== 5 ? " disabled" : ""}`}
              disabled={AuthPin.length !== 5}
            >
              Submit
            </button>
          </div>
  </div>
);
}


export function UploadDownload() { 

  const [file, setFile] = useState()

  function handleSubmit(event) {
    event.preventDefault()
    const url = 'http://localhost:3000/uploadFile';
    const formData = new FormData();
    formData.append('file', file);
    formData.append('fileName', file.name);
    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };
    axios.post(url, formData, config).then((response) => {
      console.log(response.data);
    });

  }

  function handleChange(event) {
    setFile(event.target.files[0])
  }

  function triggerFileInputClick() {
    document.getElementById('hiddenFileInput').click();
  }



return (
  <div>
    <div className="sidebar">
      <h1 className="font-link-2">Firefly</h1>
      <div className="showcode">
          <p>code:</p>
          <h1>12345</h1>
      </div>
          {
            <form onSubmit={handleSubmit}>
              <input
                type="file"
                id="hiddenFileInput"
                onChange={handleChange}
                style={{ display: 'none' }}
              />
              <button
                type="button"
                onClick={triggerFileInputClick}
                className="custom-upload-btn"
              >
                Upload File
              </button>

              <button type="button" className="custom-upload-btn">
                Download all
              </button>
            </form>
          }
          {// <Timer/>
          }
    </div>

    </div>
  );
}

export function Timer() {

    // We need ref in this, because we are dealing
    // with JS setInterval to keep track of it and
    // stop it when needed
    const Ref = useRef(null);

    // The state for our timer
    const [timer, setTimer] = useState('00:00:00');


    const getTimeRemaining = (e) => {
        const total = Date.parse(e) - Date.parse(new Date());
        const seconds = Math.floor((total / 1000) % 60);
        const minutes = Math.floor((total / 1000 / 60) % 60);
        const hours = Math.floor((total / 1000 / 60 / 60) % 24);
        return {
            total, hours, minutes, seconds
        };
    }


    const startTimer = (e) => {
        let { total, hours, minutes, seconds } 
                    = getTimeRemaining(e);
        if (total >= 0) {

            // update the timer
            // check if less than 10 then we need to 
            // add '0' at the beginning of the variable
            setTimer(
                (hours > 9 ? hours : '0' + hours) + ':' +
                (minutes > 9 ? minutes : '0' + minutes) + ':'
                + (seconds > 9 ? seconds : '0' + seconds)
            )
        }
    }


    const clearTimer = (e) => {

        // If you adjust it you should also need to
        // adjust the Endtime formula we are about
        // to code next    
        setTimer('00:15:00');

        // If you try to remove this line the 
        // updating of timer Variable will be
        // after 1000ms or 1sec
        if (Ref.current) clearInterval(Ref.current);
        const id = setInterval(() => {
            startTimer(e);
        }, 1000)
        Ref.current = id;
    }

    const getDeadTime = () => {
        let deadline = new Date();

        // This is where you need to adjust if 
        // you entend to add more time
        deadline.setSeconds(deadline.getSeconds() + 10);
        return deadline;
    }

    // We can use useEffect so that when the component
    // mount the timer will start as soon as possible

    // We put empty array to act as componentDid
    // mount only
    useEffect(() => {
        clearTimer(getDeadTime());

    // eslint-disable-next-line
    }, []);

    // Another way to call the clearTimer() to start
    // the countdown is via action event from the
    // button first we create function to be called
    // by the button


    return (
        <div className="App">
            <h2>{timer}</h2>
        </div>
    );
  };




export default App;
