import './App.css';
import { useState} from 'react';

import axios from 'axios';


function App() {
  const [activeIndex, setActiveIndex] = useState(0);
  return (
    <>
      <Panel isActive={activeIndex === 0} onShow={() => setActiveIndex(0)}>
        <Landing onAction={() => setActiveIndex(1)} />
      </Panel>
      <Panel isActive={activeIndex === 1} onShow={() => setActiveIndex(1)}>
        <UploadDownload />
      </Panel>
    </>
  );
}







export function Landing({ onAction }) { 

  const [AuthPin, setAuthPin] = useState('')

  const handleAuth = (v) => {
      if (v.length <= 5) {
          setAuthPin(v)
      }
  }

  const [, setUpload] = useState(false)
  const [, setCodesubmit] = useState(false)

  function generateCode() {
    setUpload(true)
    onAction();
  }

  function handleSubmitCode(){
    setCodesubmit(true)
    onAction();
  }

return (
  <div class="center">
    <h1 className="font-link">Firefly</h1>
          <br></br>
          <div className="button-23">
          <div className="button-24">
            {
                <button onClick={generateCode}>
                  Generate Code
                </button>
            }
          </div>

            <br></br>
            <br></br>
            <p>input pin:</p>
            <input
                value={AuthPin}
                onChange={(e) => handleAuth(e.target.value)}
                className="space"
                type="number"
                id="pin"
                pattern="\d*"
                inputMode="numeric"
            />
            <br></br>
            <br></br>
            <button
              onClick={handleSubmitCode}
              className={`button-25${AuthPin.length !== 5 ? " disabled" : ""}`}
              disabled={AuthPin.length !== 5}
            >
              Submit
            </button>
          </div>
  </div>
);
}


export function UploadDownload() { 

  const [file, setFile] = useState()

  function handleSubmit(event) {
    event.preventDefault()
    const url = 'http://localhost:3000/uploadFile';
    const formData = new FormData();
    formData.append('file', file);
    formData.append('fileName', file.name);
    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };
    axios.post(url, formData, config).then((response) => {
      console.log(response.data);
    });

  }

  function handleChange(event) {
    setFile(event.target.files[0])
  }

  function triggerFileInputClick() {
    document.getElementById('hiddenFileInput').click();
  }



return (
    <div className="sidebar">
      <h1 className="font-link-2">Firefly</h1>
      <div className="showcode">
          <p>code:</p>
          <h1>12345</h1>
      </div>
          {
            <form onSubmit={handleSubmit}>
              <input
                type="file"
                id="hiddenFileInput"
                onChange={handleChange}
                style={{ display: 'none' }}
              />
              <button
                type="button"
                onClick={triggerFileInputClick}
                className="custom-upload-btn"
              >
                Upload File
              </button>
            </form>
          }
        </div>
  );
}



export default App;
